import { useState, useEffect } from "react";
import '../styles/Liste.css';
// import bootstrap from 'bootstrap'

export default function ListePokemon(){
    const [pokemons, setPokemons] = useState([]);
    const [url, setUrl] = useState({
    current: "https://pokeapi.co/api/v2/pokemon/",
    next: null,
    previous: null,
  });
  const [image, setImage] = useState([]);

  const [tab, setTab] = useState([])

//   let [pokemonId, setPokemonId]=useState(1)//


  //PAGE SUIVANTE
  const next = () => {
    const newUrl = {
      current: url.next,
      previous: url.current,
      next: null,
    };
    setUrl(newUrl);
  };

  //PAGE PRECEDENTE
  const previous = () => {
    const newUrl = {
      current: url.previous,
      next: url.current,
      previous: null,
    };
    setUrl(newUrl);
  };


  useEffect(() => {
    fetch(url.current)
      .then((res) => res.json())
      .then((data) => {
        setPokemons(data.results);
        setUrl({
          current: url.current,
          next: data.next,
          previous: data.previous,
        });
        console.log(data);
      })
      .catch((err) => console.log(err));
    //eslint-disable-next-line
  }, [url.current]);
  
  useEffect(() => {
    

    pokemons.map((pokemon) => (
        fetch(pokemon.url)//
      .then((res) => res.json())
      .then((data) => {
        // setImage(tab.sprites.front_default);
        setImage(...current, data.sprites.front_default);
      })
    //   .current(tab) =>(...current.url)
        
      .catch((err) => console.error(err))
    ))
  }, []);
    
  // let pokemon = 6;  

//     fetch(`https://pokeapi.co/api/v2/pokemon/${pokemon}`)//
//       .then((res) => res.json())
//       .then((data) => {
//         setImage(data.sprites.front_default);
//       })
//       .catch((err) => console.error(err));
//   }, []);

  

  return (
    <div>
      <ul>
        {pokemons.map((pokemon, index) => (
            <div>
                <br></br>
                <div class="card text-bg-dark mb-3" id="test">
                    <div class="card-body">
                        <h5 class="card-title" key={index}>{index+1} - {pokemon.name}</h5>
                        <img className="img-poke" key={index} src={image[2]} alt="{pokemon}" />
                        <p class="card-text">Some quick example text to build on the card title and make up the bulk of the card's content.</p>
                    </div>
                </div>
                {/* <li key={index}>{index+1} - {pokemon.name}</li>
                <img key={index} src={image} alt="{pokemon}" /> */}
            </div>
        ))}
      </ul>
      {url.previous && <button class="btn btn-outline-dark" onClick={previous}>Previous</button>}
      {url.next && <button class="btn btn-outline-dark" onClick={next}>Next</button>}

      <br/>
      <img src={image} alt="{pokemon}" />
    </div>
  );
}
