export default function Pokedex(){

    const RandomPoke = (pokemax) => {
      return Math.floor((Math.random() * pokemax) + 1);

      var rnd = Math.floor(Math.random() * 10);
      document.getElementById('tb').value = rnd;
      
    }
    
    function Random() {
      var rnd = Math.floor(Math.random() * 10);
      document.getElementById('tb').value = rnd;
    }
    

      return (
        <div>
            <h1 className="load-h1">Mon Pokedex</h1>


            <button onClick={RandomPoke(10)} >Random Pokemon</button>
            {RandomPoke(10)}

        </div>
      );

}
