import { useState, useEffect } from "react";
import '../styles/Liste.css';
// import bootstrap from 'bootstrap'

export default function ListePokemon(){
    const [pokemons, setPokemons] = useState([]);
    const [url, setUrl] = useState({
    current: "https://pokeapi.co/api/v2/pokemon/",
    next: null,
    previous: null,
  });
  const [image, setImage] = useState([]);

//   let [pokemonId, setPokemonId]=useState(1)//


  //PAGE SUIVANTE
  const next = () => {
    const newUrl = {
      current: url.next,
      previous: url.current,
      next: null,
    };
    setUrl(newUrl);
  };

  //PAGE PRECEDENTE
  const previous = () => {
    const newUrl = {
      current: url.previous,
      next: url.current,
      previous: null,
    };
    setUrl(newUrl);
  };


  useEffect(() => {
    fetch(url.current)
      .then((res) => res.json())
      .then((data) => {
        setPokemons(data.results);
        setUrl({
          current: url.current,
          next: data.next,
          previous: data.previous,
        });
        console.log(data);
      })
      .catch((err) => console.log(err));
    //eslint-disable-next-line
  }, [url.current]);
  
  useEffect(() => {
    pokemons.map((pokemon) => (
        fetch(pokemon.url)
      .then((res) => res.json())
      .then((data) => {
        setImage((current) => [...current, data.sprites.front_default]);
      })
      .catch((err) => console.error(err))
    ))
  }, [pokemons]);
    

//   const test = () =>{
//     pokemon.na
//   }



    // let pokemon = 6;  

//     fetch(`https://pokeapi.co/api/v2/pokemon/${pokemon}`)//
//       .then((res) => res.json())
//       .then((data) => {
//         setImage(data.sprites.front_default);
//       })
//       .catch((err) => console.error(err));
//   }, []);

  

  return (
    <div>
      <ul>
        {pokemons.map((pokemon, index) => (
            // <div id="test2">
            //     <br></br>
            //     <div class="card text-bg-dark mb-3" id="test">
            //         <div class="card-body">
            //             <h5 class="card-title" key={index}>{index+1} - {pokemon.name}</h5>
            //             <img className="img-poke" src={image[index]} alt="{pokemon}" />
            //             <p class="card-text">Some quick example text to build on the card title and make up the bulk of the card's content.</p>
            //             {/* {pokemon.types[index].type.name} */}
            //         </div>
            //     </div>
            // </div>



            <div class="card-group">
              <div class="card">
                <img src="..." class="card-img-top" alt="..."/>
                <div class="card-body">
                  <h5 class="card-title">Card title</h5>
                  <p class="card-text">This is a wider card with supporting text below as a natural lead-in to additional content. This content is a little bit longer.</p>
                  <p class="card-text"><small class="text-muted">Last updated 3 mins ago</small></p>
                </div>
              </div>
            </div>
        ))}
      </ul>
      {url.previous && <button class="btn btn-outline-dark" onClick={previous}>Previous</button>}
      {url.next && <button class="btn btn-outline-dark" onClick={next}>Next</button>}

      <br/>
      <img src={image[0]} alt="{pokemon}" />
    </div>
  );
}
