import { useState, useEffect } from "react";
// import bootstrap from 'bootstrap'

export default function ListePokemon(){
    const [pokemons, setPokemons] = useState([]);
    const [url, setUrl] = useState({
    current: "https://pokeapi.co/api/v2/pokemon/",
    next: null,
    previous: null,
  });
  const [image, setImage] = useState([]);

//   let [pokemonId, setPokemonId]=useState(1)//


  //PAGE SUIVANTE
  const next = () => {
    const newUrl = {
      current: url.next,
      previous: url.current,
      next: null,
    };
    setUrl(newUrl);
  };

  //PAGE PRECEDENTE
  const previous = () => {
    const newUrl = {
      current: url.previous,
      next: url.current,
      previous: null,
    };
    setUrl(newUrl);
  };


  useEffect(() => {
    fetch(url.current)
      .then((res) => res.json())
      .then((data) => {
        setPokemons(data.results);
        setUrl({
          current: url.current,
          next: data.next,
          previous: data.previous,
        });
        console.log(data);
      })
      .catch((err) => console.log(err));
    //eslint-disable-next-line
  }, [url.current]);
  
  useEffect(() => {
    let pokemon = 1;
    
    fetch(`https://pokeapi.co/api/v2/pokemon/${pokemon}`)//
      .then((res) => res.json())
      .then((data) => {
        setImage(data.sprites.front_default);
      })
      .catch((err) => console.error(err));
  }, []);

  
  //
  return (
    <div>
      <ul>
        {pokemons.map((pokemon, index) => (
            <div>
                <div class="card text-bg-dark mb-3" style="max-width: 18rem;">
  <div class="card-header">Header</div>
  <div class="card-body">
    <h5 class="card-title">Dark card title</h5>
    <p class="card-text">Some quick example text to build on the card title and make up the bulk of the card's content.</p>
  </div>
</div>
                <li key={index}>{index+1} - {pokemon.name}</li>
                <img key={index} src={image} alt="{pokemon}" />
            </div>
        ))}
      </ul>
      {url.previous && <button class="btn btn-outline-dark" onClick={previous}>Previous</button>}
      {url.next && <button class="btn btn-outline-dark" onClick={next}>Next</button>}

      <br/>
      <img src={image} alt="{pokemon}" />
    </div>
  );
}
