import { useState, useEffect } from "react";
// import bootstrap from 'bootstrap'

export default function ListePokemon(){
    const [pokemons, setPokemons] = useState([]);
    const [url, setUrl] = useState({
    current: "https://pokeapi.co/api/v2/pokemon/",
    next: null,
    previous: null,
  });
  const [image, setImage] = useState([]);

  let [pokemonId, setPokemonId]=useState(1)//


  //PAGE SUIVANTE
  const next = () => {
    const newUrl = {
      current: url.next,
      previous: url.current,
      next: null,
    };
    setUrl(newUrl);
  };

  //PAGE PRECEDENTE
  const previous = () => {
    const newUrl = {
      current: url.previous,
      next: url.current,
      previous: null,
    };
    setUrl(newUrl);
  };


  useEffect(() => {
    fetch(url.current)
      .then((res) => res.json())
      .then((data) => {
        setPokemons(data.results);
        setUrl({
          current: url.current,
          next: data.next,
          previous: data.previous,
        });
        console.log(data);
      })
      .catch((err) => console.log(err));
    //eslint-disable-next-line
  }, [url.current]);
  
  useEffect(() => {
    let pokemon = 1;
    for (let i = 0; i < 9; i += 1)
    {
        pokemonId ++;
        fetch(`https://pokeapi.co/api/v2/pokemon/${pokemonId}`)//
      .then((res) => res.json())
      .then((data) => {
        setImage(data.sprites.front_default);
      })
      .catch((err) => console.error(err));
    }
    
  }, []);

  
  //
  return (
    <div>
      <ul>
        {pokemons.map((pokemon, index) => (
            <div>
                <li key={index}>{index+1} - {pokemon.name}</li>
                <img key={index} src={image} alt="{pokemon}" />
            </div>
        ))}
      </ul>
      {url.previous && <button onClick={previous}>Previous</button>}
      {url.next && <button onClick={next}>Next</button>}
      <br/>
      <img src={image} alt="{pokemon}" />
    </div>
  );
}
