import { useState, useEffect } from 'react';

export default function App() {
  const [pokemons, setPokemons] = useState([]);
  const [url, setUrl] = useState({
    current: "http://pokeapi.co/api/v2/pokemon/",
    next:null,
    prevuious:null,
  });

  const next = () => {
    const newUrl = {
      current: url.current,
      previous: url.next,
      next: null,
    };
    setUrl(newUrl);
  }

  const previous = () => {
    const newUrl = {
      current: url.current,
      next: url.next,
      previous: null,
    };
    setUrl(newUrl);
  }

  useEffect(() => {
    fetch(url.current)
      .then((res) => res.json())
      .then((data) => {
        setPokemons(data.results);
        setUrl({
          current: url.current,
          next: data.next,
          previous: data.previous,
        });
        console.log(data);
      })
      .catch((err) => console.error(err));
      // eslint-disable-next-line
  }, [url.current]);


  // useEffect(() => {
  //   fetch("htpps://pokeapi;co/api/v2/pokemon/6")
  //     .then((res) => res.json())
  //     .then(data => {
  //       setImage(data.sprites.front_default);

  //     })
  //     .catch((err) => console.error(err));
  //     // eslint-disable-next-line
  // }, []);


  return (
    <div className="App">
      <ul>
        {pokemons.map((pokemon) => (
          <li>{pokemon.name}</li>
        ))}
      </ul>
      {url.previous && <button onClick={previous}>Previous</button>}
      {url.next && <button onClick={next}>Next</button>}
    </div>
  );

  return (
    <View style={styles.container}>
      <Text style={styles.paragraph}>
        <h2>Pokemon API</h2>

        <Image source={{uri: `https://raw.githubusercontent.com/PokeAPI/sprites/master/sprites/pokemon/${data.id}.png`}} style={{height:200, width:200}}/>
        <Image source={{uri: `https://raw.githubusercontent.com/PokeAPI/sprites/master/sprites/pokemon/shiny/${data.id}.png`}} style={{height:200, width:200}}/>

        <Text>{data.id}</Text>/<Text>{data.name}</Text>{"\n"}<Text>Type: {data.types[0].type.name}</Text>{"\n"}<Text>Height: {data.height} dm</Text>{"\n"}<Text>Weight: {data.weight} hg</Text>{"\n"}{"\n"}{"\n"}



        <Image source={{uri: `https://raw.githubusercontent.com/PokeAPI/sprites/master/sprites/pokemon/${data.id+1}.png`}} style={{height:200, width:200}}/>
        <Image source={{uri: `https://raw.githubusercontent.com/PokeAPI/sprites/master/sprites/pokemon/shiny/${data.id+1}.png`}} style={{height:200, width:200}}/>

        <Text>{data.id+1}</Text>/<Text>{data.name}</Text>{"\n"}<Text>Type: {data.types[0].type.name}</Text>{"\n"}<Text>Height: {data.height} dm</Text>{"\n"}<Text>Weight: {data.weight} hg</Text>{"\n"}{"\n"}{"\n"}



        <Image source={{uri: `https://raw.githubusercontent.com/PokeAPI/sprites/master/sprites/pokemon/${data.id+2}.png`}} style={{height:200, width:200}}/>
        <Image source={{uri: `https://raw.githubusercontent.com/PokeAPI/sprites/master/sprites/pokemon/shiny/${data.id+2}.png`}} style={{height:200, width:200}}/>

        <Text>{data.id+2}</Text>/<Text>{data.name}</Text>{"\n"}<Text>Type: {data.types[0].type.name}</Text>{"\n"}<Text>Height: {data.height} dm</Text>{"\n"}<Text>Weight: {data.weight} hg</Text>{"\n"}{"\n"}{"\n"}


      </Text>
    </View>
  );
}
