import { useState, useEffect } from 'react';

export default function App() {
  const [pokemons, setPokemons] = useState([]);
  const [url, setUrl] = useState({
    current: "http://pokeapi.co/api/v2/pokemon/",
    next:null,
    prevuious:null,
  });

  const next = () => {
    const newUrl = {
      current: url.current,
      previous: url.next,
      next: null,
    };
    setUrl(newUrl);
  }

  const previous = () => {
    const newUrl = {
      current: url.current,
      next: url.next,
      previous: null,
    };
    setUrl(newUrl);
  }

  useEffect(() => {
    fetch(url.current)
      .then((res) => res.json())
      .then((data) => {
        setPokemons(data.results);
        setUrl({
          current: url.current,
          next: data.next,
          previous: data.previous,
        });
        console.log(data);
      })
      .catch((err) => console.error(err));
      // eslint-disable-next-line
  }, [url.current]);


  useEffect(() => {
    fetch("htpps://pokeapi;co/api/v2/pokemon/6")
      .then((res) => res.json())
