        setImage(data.sprite.front_default);

      })
      .catch((err) => console.error(err));
      // eslint-disable-next-line
  }, [url.current]);


  return (
    <div className="App">
      <ul>
        {pokemons.map((pokemon) => (
          <li>{pokemon.name}</li>
        ))}
      </ul>
      {url.previous && <button onClick={previous}>Previous</button>}
      {url.next && <button onClick={next}>Next</button>}
    </div>
  );
}
